/* eslint-disable no-nested-ternary */
/* eslint-disable no-underscore-dangle */
import React, { useState, useEffect } from "react";
import Router from "next/router";
import Styled from "styled-components";
import { Flex, Box } from "rebass";
import Dropdown from "react-dropdown";
import "react-dropdown/style.css";
import ReactTable from "react-table";
import "react-table/react-table.css";

import _x12OddsData from "@/data/1x2-odds-data";
import _1stHalfResultOddsData from "@/data/1st-half-result-odds-data";
import _bothTeamsToScoreOddsData from "@/data/both-teams-to-score-odds-data";
import _doubleChanceOddsData from "@/data/double-chance-odds-data";
import _totalGoalsOddsData from "@/data/total-goals-odds-data";

import closeIcon from "@/resources/icons/close.svg";

// Declare styled components...
const GameComponentWrapper = Styled.div`
    width: 100%;
    display: flex;
    height: 100%;
    border: 1px solid;
    position: relative;
  `;

const GameInfoContentContainer = Styled.div`
  width: 100%;
  min-height: 80vh;
  position: sticky;
  top: 80px;
  background-color: #FFFFFF;
  border: 1px solid red;
  padding: 4rem 1.5rem;
  @media screen and (max-width: 738px){
    padding: 2rem 0rem;
  }
`;

const GameInfoContentHeadingWrapper = Styled(Flex)`
  height: auto;
  @media screen and (max-width: 738px){
    background-color: ${props =>
      props.theme ? props.theme.colorTheme.grey : "inherit"};
    padding: 1rem 1.5rem;

  }
`;

const GameInfoHeadingContent = Styled(Flex)`
  display: flex;
  flex-direction: row;
`;

const GameDateContainer = Styled.span`
  display: block;
  width: 100%;
  opacity: .7;
  font-size: 1.4rem;
  margin-top: 1rem;
  text-align: left;
`;

const TeamInfoContainer = Styled(Box)`
  font-size: 1.6rem;
`;

const GameInfoContentHeadingContent = Styled(Box)`
  display: flex;
  flex-direction: column;
`;

const GameInfoCloseContainer = Styled.div`
  display: flex;
  justify-content: flex-start;
  align-items: center;
  padding: 0rem 1.5rem;
  @media screen and (max-width: 738px){
    padding-right: 0px;
  }
`;

const CloseButtonWrapper = Styled.div`
  height: 1.8rem;
  width: 1.8rem;
  margin-top: 1rem;
  @media screen and (max-width: 738px){
    height: 1.4rem;
    width: 1.4rem;
  }
`;

const CloseButton = Styled.button`
  background-image: url(${closeIcon});
  background-repeat: no-repeat;
  background-position: center;
  background-color: transparent;
  width: 100%;
  height: 100%;
  border: none;
  opacity: 0.5;
  cursor: pointer;
  transition: transform 0.4s ease-out;
  &:hover{
    transform: scale(.85) rotate(90deg);
  }
`;

const MarketControlsContainer = Styled(Flex)`
  height: auto;
  @media screen and (max-width: 738px){
    padding: 0px;
  }
`;

const MarketIndicatorWrapper = Styled(Box)``;

const MarketIndicator = Styled.span`
  background-color: rgba(240, 240, 240, 0.6);
  color: #DB4437;
  width: 100%;
  border-radius: 5px;
  height: 100%;
  display: block;
  text-align: center;
  font-size: 1.4rem;
  font-weight: 700;
  padding: .75rem 1rem;  
`;

const MarketDropdownWrapper = Styled(Box)`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const StyledDropdown = Styled(Dropdown)`
    width: 100%;
    .Dropdown-control{
    background-color: rgba(230, 230, 230, 0.7);
    font-size: 1.3rem;
  }
  .Dropdown-menu{
    font-size: 1.2rem;
    border-radius: 7px;
    max-height: auto !important;
    @media screen and (max-width: 738px){
      width: 200%;
      left: -100%;
      margin-top: 10px;
    }
    .Dropdown-option{
      padding: 12px 10px;
    }
  }
`;

const BookiesTableWrapper = Styled.div`
  width: 100%;
  min-height: 4rem;
  margin-top: 2.5rem;
  padding: .5rem 1.5rem;
  @media screen and (max-width: 738px){
    padding: 0px;
  }
`;

// Table Styles
const StyledTable = Styled(ReactTable)`
  width: auto;
  font-size: 1.4rem;
  text-align: left;
  background-color: rgba(240, 240, 240, 0.6);
  border: none !important;
  border-radius: 4px 4px 0px 0px;
  .rt-thead{
    box-shadow: none !important;
    height: 4.5rem;
    justify-content: center;
    border-bottom: 1px solid rgba(0, 0, 0, 0.1);
    min-width: 100% !important;
    width: 100% !important;
    .rt-th{
      display: flex;
      align-items: center;
      padding: 1rem 1.5rem !important;
      @media screen and (max-width: 738px){
        padding: .5rem 1rem !important;
      }
